-- Create alumni table
CREATE TABLE IF NOT EXISTS public.alumni (
  id bigint GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  name text NOT NULL,
  skill text NOT NULL,
  current_job text NOT NULL,
  years_of_experience int NOT NULL,
  linkedin_search_url text NOT NULL
);

-- Enable RLS on alumni (public read access)
ALTER TABLE public.alumni ENABLE ROW LEVEL SECURITY;

-- Everyone can read alumni data
CREATE POLICY "Anyone can view alumni" ON public.alumni
FOR SELECT USING (true);

-- Create student_profiles table
CREATE TABLE IF NOT EXISTS public.student_profiles (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id uuid REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL UNIQUE,
  name text NOT NULL,
  email text NOT NULL,
  skills text[] DEFAULT '{}',
  interested_domains text[] DEFAULT '{}',
  academic_interests text[] DEFAULT '{}',
  preferred_industries text[] DEFAULT '{}',
  profile_completed boolean DEFAULT false,
  created_at timestamp with time zone DEFAULT now(),
  updated_at timestamp with time zone DEFAULT now()
);

-- Enable RLS on student_profiles
ALTER TABLE public.student_profiles ENABLE ROW LEVEL SECURITY;

-- Students can view their own profile
CREATE POLICY "Students can view own profile" ON public.student_profiles
FOR SELECT USING (auth.uid() = user_id);

-- Students can insert their own profile
CREATE POLICY "Students can insert own profile" ON public.student_profiles
FOR INSERT WITH CHECK (auth.uid() = user_id);

-- Students can update their own profile
CREATE POLICY "Students can update own profile" ON public.student_profiles
FOR UPDATE USING (auth.uid() = user_id);

-- Create mentor_selections table
CREATE TABLE IF NOT EXISTS public.mentor_selections (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  student_id uuid REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  alumni_id bigint REFERENCES public.alumni(id) ON DELETE CASCADE NOT NULL,
  match_score numeric NOT NULL,
  match_explanation text,
  selected_at timestamp with time zone DEFAULT now(),
  UNIQUE(student_id, alumni_id)
);

-- Enable RLS on mentor_selections
ALTER TABLE public.mentor_selections ENABLE ROW LEVEL SECURITY;

-- Students can view their own selections
CREATE POLICY "Students can view own selections" ON public.mentor_selections
FOR SELECT USING (auth.uid() = student_id);

-- Students can insert their own selections
CREATE POLICY "Students can insert own selections" ON public.mentor_selections
FOR INSERT WITH CHECK (auth.uid() = student_id);

-- Create function to update timestamps
CREATE OR REPLACE FUNCTION public.update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
  NEW.updated_at = now();
  RETURN NEW;
END;
$$ LANGUAGE plpgsql SET search_path = public;

-- Create trigger for automatic timestamp updates
CREATE TRIGGER update_student_profiles_updated_at
BEFORE UPDATE ON public.student_profiles
FOR EACH ROW
EXECUTE FUNCTION public.update_updated_at_column();

